mapper.update:
update news
sender_id = #{senderId,jdbcType=INTEGER}, recipient_id = #{recipientId,jdbcType=INTEGER}, is_user = #{whetherUser,jdbcType=BIT}, content = #{content,jdbcType=VARCHAR}, create_time = #{createTime,jdbcType=TIMESTAMP}, update_time = #{updateTime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, sender_id, recipient_id, is_user, content, create_time, update_time
from news
where id = #{id,jdbcType=INTEGER}
